import React, { Component } from 'react';
import Navbar from './navbar';
import Axios from 'axios';
import { Redirect , Link} from 'react-router-dom';
import jwt from 'jsonwebtoken';
import Loader from './loader';

export default class details extends Component {
    constructor(props){
        super(props);
        this.state={
            phone : [],
            total:0,
            loading: false
        }
    }
    increment=async (index)=>{
        this.setState({
            loading: true
        })

       const phone = await this.state.phone.filter(eachPhone=>{
            if(eachPhone.productId === index){
                eachPhone.productQuantity++;
                return true;
            }
            return true
        })

        this.setState({
            phone : phone
        })

        const user = await jwt.decode(localStorage.getItem('user'),'secret');
        let data = await Axios.put(`http://localhost:2406/AddData/updateInc/${user._id}/${index}/1`);
        console.log(data.data);
        this.setState({
            loading: false
        })

      };

      decrement=async (index)=>{
        this.setState({
            loading: true
        })
        var flag=1;
        const phone = await this.state.phone.filter(eachPhone=>{
             if(eachPhone.productId === index){
                 if(eachPhone.productQuantity>1)
                {
                 eachPhone.productQuantity--;
                    flag=1;
                    return true;
                }
                else
                {
                    eachPhone.productQuantity=1;
                    flag=0;
                    alert("Quantity can't be less than one, Please trying removing the item from the cart");  
                    return false;
                }
             }
             return true
         })
        if(flag===1)
        {
            this.setState({
                phone : phone
            })
            const user = await jwt.decode(localStorage.getItem('user'),'secret');
            let data = await Axios.put(`http://localhost:2406/AddData/updateDec/${user._id}/${index}/1`);   
            console.log(data.data);
        }
        this.setState({
            loading: false
        })
       };
//     const phone = await this.state.phone.filter(eachPhone=>{
//         if(eachPhone.productId === index){
//             eachPhone.productQuantity--;
//             return true;
//         }
//         return true
//     })

//     this.setState({
//         phone : phone
//     })

//     const user = await jwt.decode(localStorage.getItem('user'),'secret');
//     let data = await Axios.put(`http://localhost:2406/AddData/updateDec/${user._id}/${index}/1`);
//     console.log(data.data);

//   };

      del=async (index)=>{
        this.setState({
            loading: true
        })
        const user = await jwt.decode(localStorage.getItem('user'),'secret');
        Axios.delete(`http://localhost:2406/AddData/deleteCart/${user._id}/${index}`);
        const phone = this.state.phone.filter(eachPhone=>{
            if(eachPhone.productId!==index){
                return eachPhone
            }
            return false
        })

        this.setState({
            phone : phone
        })
        this.setState({
            loading: false
        })
      }

    // componentDidMount(){
    //     // console.log(this.props);
    //     // console.log(this.props.match.params.id)
    //     Axios.put(`http://localhost:2406/AddData/update/${this.props.match.params.id}`)
    //     // .then(res=>{
    //     //     console.log(res.data);
    //     //     this.setState({
    //     //         phone : res.data.phone
    //     //     })
    //     // })
    //     // .catch(err=>{
    //     //     console.log(err);
    //     // })
    // }

    componentDidMount(){
        Axios.get('http://localhost:2406/AddData/fetchtoCart',{
            headers : {
                'authorization' : "token " + localStorage.user
            }
        })
        .then(res=>{
            console.log(res.data)
            this.setState({
                phone: res.data
            })
        })
    }
    render() {
        if(localStorage.getItem('user'))
        {
            var total=0;

            if(this.state.loading){
                return (
                    <Loader />
                )
            }
            else if(this.state.phone.length===0){
                return(
                    <div>
                        <h1>
                            Tere cart khali hai
                        </h1>
                    </div>
                )
            }
            else{
        return(
            <div>
                <Navbar/>
                    <h1>    
                        {this.state.phone.map((prod, index) => {

                            return(
                                <div key={index}>
                                    <img src={prod.img} alt='loading img'/>
                                    {prod.title}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    {prod.price}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    <button onClick={()=>this.increment(prod.productId)}>+</button>
                                    &nbsp;&nbsp;&nbsp;{prod.productQuantity}&nbsp;&nbsp;&nbsp;
                                    <button onClick={()=>this.decrement(prod.productId)}>-</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    <button onClick={()=>this.del(prod.productId)}>Delete</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    {prod.price*prod.productQuantity}
                                    <div style={{display:"none"}}>
                                    {total+= prod.price*prod.productQuantity}
                                    </div>
                                </div>
                            )
                        })}
                        <div style={{marginTop:"50px",marginLeft:"50px",marginBottom:"50px",float:"left"}}>
                            Subtotal: {total}
                            <Link to={`/home/buynow/${total}`}>
                            <button style={{marginLeft:"100vh"}}>Buy Now</button>
                            </Link>
                        </div>
                    </h1>
            </div>
        )
    }}
else
{
    return (
    <Redirect to='/' />
    )
}
}
}
